import React from "react";
import { FaSearch, FaPhone, FaEllipsisV, FaPaperclip, FaPaperPlane, FaSmile } from "react-icons/fa";
import Message from "./Message";

class Chat extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            msgs:[
                {
                    received: true,
                    text:"ahahahahhahahaah",
                    time:"21:36"
                },
                {
                    received: true,
                    text:"cool => {REALLY}",
                    time:"21:36"
                }
            ]
        }
    }
    
    render(){
        return(
            <>
                <header>
                    <div className="float-left">
                        <img alt=""></img>
                        <h3>Fasad Salatov</h3>
                        <p>last seen recently</p>
                    </div>
                    <div className="float-right">
                        <FaSearch/>
                        <FaPhone/>
                        <FaEllipsisV/>
                    </div>
                </header>
                <main>
                    {this.state.msgs.map((msg) => (
                        <Message received={msg.received} text={msg.text} time={msg.time}/>
                    ))}
                </main>
                <footer>
                    <form>
                        <FaPaperclip/>
                        <input type="text" placeholder="Write a message"></input>
                        <FaSmile/>
                        <FaPaperPlane/>
                    </form>
                </footer>
            </>
        )
    }
}

export default Chat
