import React from "react";
import { FaSearch, FaPhone, FaEllipsisV } from "react-icons/fa";
import Entry from "./Entry";
import Message from "./Message";

import logo from "../img/logo.png"

class Chat extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            msgs:[
                {
                    id: 0,
                    received: true,
                    text:"ahahahahhahahaah",
                    time:"21:36"
                },
                {
                    id: 1,
                    received: true,
                    text:"cool => {REALLY}",
                    time:"21:37"
                }
            ]
        }
        this.addMsg = this.addMsg.bind(this)
    }
    
    render(){
        return(
            <div className="chat">
                <header>
                    <div className="float-left flex-row-nowrap">
                        <img src={logo} className="logo" alt=""></img>
                        <div className="flex-column-nowrap">
                            <h3>Fasad Salatov</h3>
                            <p>last seen recently</p>
                        </div>
                    </div>
                    <div className="float-right">
                        <FaSearch className="icon"/>
                        <FaPhone className="icon"/>
                        <FaEllipsisV className="icon"/>
                    </div>
                </header>
                <main>
                    {this.state.msgs.map((msg) => (
                        <Message msg={msg} key={msg.id}/>
                    ))}
                </main>
                <footer>
                    <Entry addMsg={this.addMsg}/>
                </footer>
            </div>
        )
    }
    addMsg(msg){
        const now = new Date()
        const time = now.toLocaleTimeString()
        const id = () => {
            let maxId = 0;
            for(let i=0; i < this.msgs.length; i++){
                if(this.msgs[i].id>maxId) maxId = this.msgs[i].id
            }
        }
        this.setState({ msgs: [...this.state.msgs, {id, time, ...msg}] })
    }
    removeMsg(id){
        this.setState({
            msgs: this.state.msgs.filter((el) => el.id !== id)
        })
    }
}

export default Chat
