import React from "react";

function getInitials(name) {
    const nameParts = name.split(' ');
    return nameParts.map(part => part[0].toUpperCase()).join('');
}
  
function getRandomColor() {
    const colors = ['#FF5733', '#C70039', '#900C3F', '#581845'];
    const randomIndex = Math.floor(Math.random() * colors.length);
    return colors[randomIndex];
}

class Item extends React.Component{
    render(){
        const list = this.props.list
        return(
            <div className="Item flex-row-nowrap">
                <span className="logo">{list.logo === null ? getInitials(list.name) : <img src={list.logo} alt=""></img>}</span>
                <div className="flex-column-nowrap">
                    <h3>{list.name}</h3>
                    <p>{list.lastMessage}</p>
                </div>
            </div>
        )
    }
}

export default Item
