import React from "react";
import {FaPaperclip, FaPaperPlane, FaSmile} from "react-icons/fa";

class Entry extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            text:""
        }
    }
    
    render(){
        return (
            <form className="flex-row-nowrap" ref={(el) => this.entry = el}>
                <FaPaperclip className="icon"/>
                <textarea 
                    className="tinput"
                    onChange={(event) => this.setState({text: event.target.value})}
                    type="text" rows="1"
                    placeholder="Write a message"
                    onKeyUp={(event) => {if(event.key==="Enter") console.log(this.entry.style.height)}}
                    />
                <FaSmile className="icon"/>
                <FaPaperPlane className="icon" onClick={() => {
                    this.props.addMsg({
                        received: false,
                        text:this.state.text,
                    })
                    this.entry.reset()
                }}/>
            </form>
        )
    }
}

export default Entry
