import React from "react";
import { FaSearch, FaPhone, FaEllipsisV, FaPaperclip, FaPaperPlane, FaSmile } from "react-icons/fa";
import Entry from "./Entry";
import Message from "./Message";

class Chat extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            msgs:[
                {
                    id: 0,
                    received: true,
                    text:"ahahahahhahahaah",
                    time:"21:36"
                },
                {
                    id: 1,
                    received: true,
                    text:"cool => {REALLY}",
                    time:"21:36"
                }
            ]
        }
    }
    
    render(){
        return(
            <>
                <header>
                    <div className="float-left">
                        <img alt=""></img>
                        <h3>Fasad Salatov</h3>
                        <p>last seen recently</p>
                    </div>
                    <div className="float-right">
                        <FaSearch/>
                        <FaPhone/>
                        <FaEllipsisV/>
                    </div>
                </header>
                <main>
                    {this.state.msgs.map((msg) => (
                        <Message msg={msg}/>
                    ))}
                </main>
                <footer>
                    <Entry/>
                </footer>
            </>
        )
    }
}

export default Chat
